mapper.select:
select id, user_id, order_number, channel_payment_id, channel_type, amount, pay_type,
pay_status, extend_str, pay_end_time, gmt_created, gmt_modified
from ykd_payment_record


        and user_id = #{userId,jdbcType=VARCHAR}


        and order_number = #{orderNumber,jdbcType=VARCHAR}


        and pay_type = #{payType}


        and pay_status = #{paymentStatus}
